#import <Foundation/Foundation.h>
#import <Capacitor/Capacitor.h>

// Define the plugin using the CAP_PLUGIN Macro, and
// each method the plugin supports using the CAP_PLUGIN_METHOD macro.
CAP_PLUGIN(RookSDKPlugin, "RookSDK",
           CAP_PLUGIN_METHOD(initRook, CAPPluginReturnPromise);
           CAP_PLUGIN_METHOD(updateUserId, CAPPluginReturnPromise);
           CAP_PLUGIN_METHOD(getUserId, CAPPluginReturnPromise);
           CAP_PLUGIN_METHOD(clearUserId, CAPPluginReturnPromise);
           CAP_PLUGIN_METHOD(deleteUserFromRook, CAPPluginReturnPromise);
           CAP_PLUGIN_METHOD(enableAppleHealthSync, CAPPluginReturnPromise);
           CAP_PLUGIN_METHOD(disableAppleHealthSync, CAPPluginReturnPromise);
           CAP_PLUGIN_METHOD(isAppleHealthSyncEnable, CAPPluginReturnPromise);
           CAP_PLUGIN_METHOD(syncUserTimeZone, CAPPluginReturnPromise);
           CAP_PLUGIN_METHOD(checkAvailability, CAPPluginReturnPromise);
           CAP_PLUGIN_METHOD(requestAllAppleHealthPermissions, CAPPluginReturnPromise);
           CAP_PLUGIN_METHOD(requestAllHealthConnectPermissions, CAPPluginReturnPromise);
           CAP_PLUGIN_METHOD(requestAllPermissions, CAPPluginReturnPromise);
           CAP_PLUGIN_METHOD(openIOSAppleHealth, CAPPluginReturnPromise);
           CAP_PLUGIN_METHOD(openHealthConnectSettings, CAPPluginReturnPromise);
           CAP_PLUGIN_METHOD(healthConnectHasPermissions, CAPPluginReturnPromise);
           CAP_PLUGIN_METHOD(requestAndroidBackgroundPermissions, CAPPluginReturnPromise);
           CAP_PLUGIN_METHOD(enableBackGroundUpdates, CAPPluginReturnPromise);
           CAP_PLUGIN_METHOD(disableBackGroundUpdates, CAPPluginReturnPromise);
           CAP_PLUGIN_METHOD(shouldSyncFor, CAPPluginReturnPromise);
           CAP_PLUGIN_METHOD(syncSummaries, CAPPluginReturnPromise);
           CAP_PLUGIN_METHOD(syncSleepSummary, CAPPluginReturnPromise);
           CAP_PLUGIN_METHOD(syncPhysicalSummary, CAPPluginReturnPromise);
           CAP_PLUGIN_METHOD(syncBodySummary, CAPPluginReturnPromise);
           CAP_PLUGIN_METHOD(reSyncFailedSummaries, CAPPluginReturnPromise);
           CAP_PLUGIN_METHOD(syncBodyHeartRateEvents, CAPPluginReturnPromise);
           CAP_PLUGIN_METHOD(syncPhysicalHeartRateEvents, CAPPluginReturnPromise);
           CAP_PLUGIN_METHOD(syncBodyOxygenationEvents, CAPPluginReturnPromise);
           CAP_PLUGIN_METHOD(syncPhysicalOxygenationEvents, CAPPluginReturnPromise);
           CAP_PLUGIN_METHOD(syncPhysicalEvents, CAPPluginReturnPromise);
           CAP_PLUGIN_METHOD(syncTemperatureEvents, CAPPluginReturnPromise);
           CAP_PLUGIN_METHOD(syncBloodPressureEvents, CAPPluginReturnPromise);
           CAP_PLUGIN_METHOD(syncBloodGlucoseEvents, CAPPluginReturnPromise);
           CAP_PLUGIN_METHOD(syncBodyMetricsEvents, CAPPluginReturnPromise);
           CAP_PLUGIN_METHOD(syncPendingEvents, CAPPluginReturnPromise);
           CAP_PLUGIN_METHOD(syncEvents, CAPPluginReturnPromise);
)
